import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function Reco() {
  const chart1 = useRef(null);
  const chart2 = useRef(null);
  const chart3 = useRef(null);

  const chartData = [{
    charNo: chart1, 
    percent: 80, 
    bgColor: '#F5D042'
  }, 
  {
    charNo: chart2, 
    percent: 40, 
    bgColor: '#0A174E'
  }, 
  {
    charNo: chart3, 
    percent: 30, 
    bgColor: '#EC8B5E'
  }, 
]

  useEffect(() => {
    charData.map((row) => {
      makeChart(row.percent, row.chartNo, row.bgColor);
    })
  }, []);


  let i = 1;
  const makeChart = (percent, className, color) => {
    let func1 = setInterval(function(){
      if(i<percent){
        if (className.current){
          color1(i,className,color);
          i++;
        } else {
          return;
        }
      } else{
        clearInterval(func1);
      }
    },10);
  }


  function color1(i, className,color){
      className.css({
          "background":"conic-gradient("+color+" 0% "+i+"%, #dedede "+i+"% 100%)"
      });
  }
  
  return (
    <div>
      <div ref = {chart1}>
        <div> { chartData[0].percent} % </div>
      </div>
      <div ref = {chart2}>
        <div> { chartData[1].percent} % </div>
      </div>
      <div ref = {chart2}>
        <div> { chartData[2].percent} % </div>
      </div>
    </div>
  );
};

export default Reco;
