import React, { useState } from 'react';
import './App.css';
import Auth from "./Auth";
import { BrowserRouter as Router, Route } from "react-router-dom";

function App() {
  const REST_API_KEY = "[f086ee1ebebb17e22a9d2e6ec35fb062]";
  const REDIRECT_URI = "http://127.0.0.1:5500/public/index.html";
  const KAKAO_AUTH_URL = `https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`;
  
  return (
    <div className="App">
      <Header></Header>
      <Nav></Nav>
      <Router>
        <Routes>
          <Route exact path="/" element = {<h1><a href={KAKAO_AUTH_URL}>Kakao Login</a></h1>}></Route>
          <Route path="http://127.0.0.1:5500/public/index.html" element = {<Auth />}></Route>
        </Routes>
      </Router>
    </div>
  );
}

function Header(props) {
  return (
    <div>
      <h1>주말농장 치유농장 체험농장</h1>
    </div>
  );
}

function Nav(props) {
  return (
    <div>
      <div>소개</div>
      <div>농작물로 치유하기</div>
      <div>레시피</div>
    </div>
  );
}

function Kakaologin(props) {
  const {Kakao} = window;
  const loginWithKakao = () => {
    Kakao.Auth.authorize({
      redirectUri: 'https://developers.kakao.com/tool/demo/oauth'
    })
  }

  const KakaoLogin = () => {
    return (
      <div>
        <a id = "login-btn" onClick = {loginWithKakao}>
          <img src = "kakao_login_medium_narrow.png"/>
        </a>
      </div>
    );
  };
}

export default App;
