import { ConnectedRouter } from 'connected-react-router';
import { Route, Switch } from 'react-router-dom';
import history from './history';
import KakaoRedirectHandler from './kakao/KakaoRedirectHandler';
import Home from './pages/Home';
import Signin from './pages/Signin';
import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <Header></Header>
      <Nav></Nav>
      <Login></Login>
      <script src="https://developers.kakao.com/sdk/js/kakao.js"></script>
      <ConnectedRouter history={history}>
        <Switch>
          <Route exact path="/signin" component={Signin} />
          <Route exact path="/" component={Home} />
          <Route path="/oauth/callback/kakao" component={KakaoRedirectHandler} />
        </Switch>
      </ConnectedRouter>
    </div>
  );
}
function Header(props) {
  return (
    <div>
      <h1>주말농장 치유농장 체험농장</h1>
    </div>
  );
}
function Nav(props) {
  return (
    <div>
      <span>소개</span>
      <span>농작물로 치유하기</span>
      <span>레시피</span>
    </div>
  );
}
function Login(props) {
  <div>
    <a href="kakaoLogin();"><img src="kakao_login_medium_narrow.png"></img></a>
    <p id="nickname"></p>
  </div>
}

function kakaoLogin() {
    window.Kakao.Auth.login({
        scope: 'profile_nickname, account_email, gender', 
        success: function(authObj) {
            window.Kakao.API.request({
                url:'/v2/user/me',
                success: res => {
                    const kakao_account = res.kakao_account;
                    document.getElementById("nickname").innerHTML = kakao_account.profile.nickname+'님 반갑습니다!';
                }
            });
        }
    });
}

export default App;
